// Dispatch SetTarget
// Once SetTarget Is Complete, 
import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux'
import optionsSlice from '../Store/optionsSlice';

const asyncDispatcher = payload =>{

  const dispatch = useDispatch()

  Promise.resolve(dispatch(targetStore(payload))).then(
    () => dispatch(optionsStore())
  )
}

export default asyncDispatcher;
