import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import React from 'react';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { Header } from 'react-native-elements';
import { StyleSheet, Text, SafeAreaView, FlatList, View, TextInput } from 'react-native';

class Engine extends React.Component {

  constructor(props) {

    super(props);

    this.state = {
      target: 0,
      FourHundred: 6.49,
      EightHundred: 12.99,
      OneThousandSevenHundred:25.99,
      // Array of Key&Value Pairs
      Options:[{options:'',price:''}], 
    };

    this.handleChange=this.handleChange.bind(this);
    this.returnOptionsByFactors=this.returnOptionsByFactors.bind(this);
    this.returnOptionsByUniqueSet=this.returnOptionsByUniqueSet.bind(this);
    this.returnOptionsByRandomnMethod=this.returnOptionsByRandomnMethod.bind(this);
    this.formOptions=this.formOptions.bind(this);
    this.returnRenderItem=this.returnRenderItem.bind(this);

  }
  handleChange=(text)=>{
    console.log(text);
  }

  returnOptionsByFactors=()=>{}
  returnOptionsByUniqueSet=()=>{}
  returnOptionsByRandomnMethod=()=>{}
  formOptions=()=>{}
  returnRenderItem=()=>{
    return <View style={styles.item}><Text>Item</Text></View>;
  }

  render() {
    return (

      <SafeAreaProvider>
        <SafeAreaView>
          <View style={styles.container}>

            {/* Header */}
            <Header
              leftComponent={{ icon: 'menu', color: '#fff' }}
              centerComponent={{ text: 'MY TITLE', style: { color: '#fff' } }}
              rightComponent={{ icon: 'home', color: '#fff' }}
            />

            <Text>Enter Target: </Text>
            <TextInput onChangeText={this.handleChange} placeholder="target"></TextInput>
            <StatusBar style="auto" />

            <FlatList data={this.state.Options} renderItem={this.returnRenderItem}></FlatList>

            {/* List View */}
          </View>
        </SafeAreaView>
      </SafeAreaProvider>

    );
  }
}

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    marginTop: '20%',
    backgroundColor: '#fff',
  },

  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },

});

export default Engine;
